SELECT ock.client_key, ock.secret,
                ock.scope, ock.callback, ock.environment, ock.expiration, ock.secret_expiration, ock.status, ock.client_ident, FROM_UNIXTIME(ock.created, "%Y-%m-%d %k:%i:%s") as created,
                ock.created_by, ock.client_name, ock.custom, ock.serviceIds, ock.accountPlanMappingIds, ock.masterkey_flag, ock.hashing_metadata, ock.resources
                FROM oauth_client_key ock
                WHERE ock.created_by like ${registered_by}
                AND ock.status = ${filterStatus}
                AND ock.environment like ${environment}
                AND ock.client_ident = ${client_ident}
                ORDER BY ock.created_by, ock.created, ock.expiration ASC LIMIT 100 OFFSET ${offset}

SELECT client_key, secret, scope, callback, environment, expiration, secret_expiration, status, client_ident, TO_CHAR(TO_DATE('19700101000000', 'YYYYMMDDHH24MISS')+(created/(60*60*24)),'YYYY-MM-DD HH24:MI:SS') as created, created_by, client_name, custom, serviceIds, accountPlanMappingIds,masterkey_flag,resources
                FROM (
                SELECT /*+ FIRST_ROWS(100) */ oauth_client_key.*, ROWNUM rnum
                FROM ( 	SELECT * FROM oauth_client_key
                WHERE created_by like ${registered_by}
                AND status = ${filterStatus}
                AND environment like ${environment}
                AND client_ident = ${client_ident}
                ORDER BY created_by, created, expiration ASC ) oauth_client_key
                WHERE ROWNUM <= (100+${offset}) )
                WHERE rnum  > ${offset}

