select client_key, secret, scope, callback, environment, expiration, secret_expiration, status, client_ident, TO_CHAR(TO_DATE('19700101000000', 'YYYYMMDDHH24MISS')+(created/(60*60*24)),'YYYY-MM-DD HH24:MI:SS') as created,created_by, client_name from, custom, serviceIds, accountPlanMappingIds, masterkey_flag, hashing_metadata, resources
                ( select oauth_client_key.*, ROWNUM rnum from
                ( select ock.client_key, ock.secret,ock.scope, ock.callback, ock.environment, ock.expiration, ock.secret_expiration, ock.status, ock.client_ident, ock.created,ock.created_by, ock.client_name, ock.custom, ock.serviceIds, ock.accountPlanMappingIds, ock.masterkey_flag, ock.hashing_metadata, ock.resources from oauth_client_key ock LEFT OUTER JOIN
                oauth_client oc ON ock.client_ident = oc.client_ident
                WHERE oc.organization like ${org}
                AND oc.name like ${name}
                ORDER BY ock.client_name, ock.created_by, ock.created, ock.expiration ASC  ) oauth_client_key
                where ROWNUM <= (100+${offset}))
                where rnum  > ${offset}

